import React, { useState } from 'react';
import { Form, Button, Alert } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { useForm } from 'react-hook-form';

const Login = () => {
  const { register, handleSubmit, reset, formState: { errors } } = useForm();
  const [show, setShow] = useState(false);
  const [serverResponse, setServerResponse] = useState('');

  const submitForm = (data) => {
    const body = {
      email: data.email,
      password: data.password
    };

    const requestOptions = {
      method: "POST",
      headers: {
        'content-type': 'application/json'
      },
      body: JSON.stringify(body)
    };

    fetch('/auth/login', requestOptions)
      .then(res => res.json())
      .then(data => {
        console.log(data);
        setServerResponse(data.message);
        setShow(true);
      })
      .catch(err => console.log(err));

    reset();
  };

  return (
    <div className="container">
      <div className="form">
        {show ?
          <>
            <Alert variant="success" onClose={() => { setShow(false) }} dismissible>
              <p>
                {serverResponse}
              </p>
            </Alert>

            <h1>Login Page</h1>
          </>
          :
          <h1>Login Page</h1>
        }
        <form>
          <Form.Group>
            <Form.Label>Email</Form.Label>
            <Form.Control type="email"
              placeholder="Your email"
              {...register("email", { required: true, maxLength: 80 })}
            />

            {errors.email && <p style={{ color: "red" }}><small>Email is required</small></p>}

            {errors.email?.type === "maxLength" && <p style={{ color: "red" }}><small>Max characters should be 80</small></p>}
          </Form.Group>
          <br></br>
          <Form.Group>
            <Form.Label>Password</Form.Label>
            <Form.Control type="password"
              placeholder="Your password"
              {...register("password", { required: true, minLength: 8 })}
            />

            {errors.password && <p style={{ color: "red" }}><small>Password is required</small></p>}
            {errors.password?.type === "minLength" && <p style={{ color: "red" }}><small>Min characters should be 8</small></p>}
          </Form.Group>
          <br></br>
          <Form.Group>
            <Button as="sub" variant="primary" onClick={handleSubmit(submitForm)}>Login</Button>
          </Form.Group>
          <br></br>
          <Form.Group>
            <small>Don't have an account, <Link to='/sign-up'>Sign Up</Link></small>
          </Form.Group>
          <br></br>
        </form>
      </div>
    </div>
  );
};

export default Login;
