import React, { useState } from 'react';
import axios from 'axios';

const DictionarySearch = () => {
  const [word, setWord] = useState('');
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleInputChange = (e) => {
    setWord(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!word) {
      return;
    }

    setLoading(true);
    setError(null);

    try {
      const response = await axios.get(`https://your-dictionary-api.com/api/lookup?word=${word}`);
      setResult(response.data);
    } catch (error) {
      console.error('An error occurred:', error);
      setError('An error occurred while searching for the word.');
    }

    setLoading(false);
  };

  return (
    <div>
      <h2>Dictionary Search</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" value={word} onChange={handleInputChange} placeholder="Enter a word" />
        <button type="submit" disabled={loading}>
          {loading ? 'Searching...' : 'Search'}
        </button>
      </form>
      {error && <p>{error}</p>}
      {result && (
        <div>
          <h3>{result.word}</h3>
          <p>{result.definition}</p>
        </div>
      )}
    </div>
  );
};

export default DictionarySearch;
